mapper.update:
update ACCOUNT
set USER_NAME = #{userName,jdbcType=VARCHAR},
  USER_PWD = #{userPwd,jdbcType=VARCHAR},
  USER_PHONE = #{userPhone,jdbcType=VARCHAR},
  USER_ROOT = #{userRoot,jdbcType=TINYINT}
where USER_ID = #{userId,jdbcType=INTEGER}

mapper.select:
SELECT  COUNT(*)
from ACCOUNT as A
where A.USER_NAME = #{userName,jdbcType=VARCHAR}
and A.USER_PWD = #{userPwd,jdbcType=VARCHAR}

SELECT  COUNT(*)
from ACCOUNT A
where A.USER_PHONE = #{userPhone,jdbcType=VARCHAR}
and A.USER_PWD = #{userPwd,jdbcType=VARCHAR}

SELECT a.USER_ID as userId, a.USER_NAME AS userName, a.USER_PHONE as userPhone, a.USER_ROOT as userRoot, u.U_ADDRESS as uAddress, u.U_DIS as uDis, u.U_BG as uBg, u.U_TX as uTx, u.U_SEX as uSex, u.U_NICKNAME as uNickname, u.U_CREATETIME as uCreatetime, (SELECT COUNT(*) from USER_FOLLOW_REL as f where f.USER_ID = a.USER_ID) as followNum, (SELECT COUNT(*) from USER_FOLLOW_REL as f where f.USER_FOLLOW_ID = a.USER_ID) as fansNum
FROM ACCOUNT AS a, USER_INFO AS u WHERE a.USER_ID = u.USER_ID
AND a.USER_ID = #{userId,jdbcType=INTEGER}
AND a.USER_NAME = #{userName,jdbcType=VARCHAR}
AND u.U_NICKNAME = #{uNickname,jdbcType=VARCHAR}
and u.U_CREATETIME >= #{qryStartDate,jdbcType=VARCHAR}
and u.U_CREATETIME <= #{qryEndDate,jdbcType=VARCHAR}
LIMIT #{start,jdbcType=INTEGER} ,#{limit,jdbcType=INTEGER};

SELECT count(*) from ( SELECT a.USER_ID as userId, a.USER_NAME AS userName, a.USER_PHONE as userPhone, a.USER_ROOT as userRoot, u.U_ADDRESS as uAddress, u.U_DIS as uDis, u.U_BG as uBg, u.U_TX as uTx, u.U_SEX as uSex, u.U_NICKNAME as uNickname, u.U_CREATETIME as uCreatetime, (SELECT COUNT(*) from USER_FOLLOW_REL as f where f.USER_ID = a.USER_ID) as followNum, (SELECT COUNT(*) from USER_FOLLOW_REL as f where f.USER_FOLLOW_ID = a.USER_ID) as fansNum
FROM ACCOUNT AS a, USER_INFO AS u WHERE a.USER_ID = u.USER_ID
AND a.USER_ID = #{userId,jdbcType=INTEGER}
AND a.USER_NAME = #{userName,jdbcType=VARCHAR}
AND u.U_NICKNAME = #{uNickname,jdbcType=VARCHAR}
and u.U_CREATETIME >= #{qryStartDate,jdbcType=VARCHAR}
and u.U_CREATETIME <= #{qryEndDate,jdbcType=VARCHAR}
) as t
