mapper.select:
SELECT * FROM ( SELECT g.PIC_DESCRIBE as picDescribe, g.PIC_GROUP_ID as picGroupId, g.PIC_GROUP_STATE as picGroupState, g.PIC_TITLE as picTitle, g.PIC_UP_DATE as picUpDate, g.USER_ID as userId, li.PIC_LABEL_NAME as picLabelName, li.PIC_LABEL_ID as picLabelId, u.U_NICKNAME as uNickname, ( SELECT COUNT(*) FROM PIC_COMMENT_REL AS c WHERE c.PIC_GROUP_ID = g.PIC_GROUP_ID ) AS commentNum, ( SELECT COUNT(*) FROM PIC_LIKE_REL AS l WHERE l.PIC_ID = g.PIC_GROUP_ID ) AS likeNum, ( SELECT PIC_INFO.PIC_ADDRESS FROM PIC_GROUP_INFO, PIC_INFO WHERE g.PIC_GROUP_ID = PIC_INFO.PIC_GROUP_ID LIMIT 0,1 ) AS picAdress FROM PIC_GROUP_INFO AS g, PIC_LABEL_INFO AS li, PIC_LABEL_REL AS lr, USER_INFO as u WHERE g.PIC_GROUP_ID = lr.PIC_GROUP_ID AND lr.PIC_LABEL_ID = li.PIC_LABEL_ID AND u.USER_ID = g.USER_ID ) a
WHERE 1=1
and PIC_TITLE = #{picTitle jdbcType=VARCHAR}
and USER_ID = #{userId jdbcType=INTEGER}
and PIC_LABEL_NAME = #{picLabelName jdbcType=VARCHAR}
and PIC_LABEL_ID = #{picLabelId jdbcType=INTEGER}
and PIC_UP_DATE >= #{qryStartDate dbcType=VARCHAR}
and PIC_UP_DATE <= #{qryEndDate dbcType=VARCHAR}
LIMIT #{start jdbcType=INTEGER} ,#{end jdbcType=INTEGER};
